const upload = function(filename, callback) {
  if (!filename) {
    throw 'You need a filename!';
  } else {
    readFile(filename, function(err, contents) {
      if (err) throw err;
      httpPost(contents, function(err, json) {
        if (err) throw err;
        callback(json);
      });
    });
  }
};


